<script>

    ///////////////////EVENEMENTEN//////////////////////

    var crmTabel = document.getElementById('vw-crm-table');

    crmTabel.addEventListener('click', function(e){

        var t = e.target;

        if (t.classList.contains('icono-tag')) {
        	t = t.parentNode;
        }

        var c = t.classList;
        var pc = t.parentNode.classList;
        var rij = vindTr(t);

        if (c.contains('functioneel') || pc.contains('functioneel')) {
            if (c.contains('aanpassen') || pc.contains('aanpassen')) {

                if (!c.contains('wordt-aangepast')) {
                	console.log('pas aan');
                    pasRijAan(rij);
                } else {
                	console.log('sla op');
                    slaRijOp(rij);
                }

                c.toggle('wordt-aangepast');

            } else if (c.contains('verwijderen') || pc.contains('verwijderen')) {

                if (confirm('Bevestig verwijderen ' + rij.querySelectorAll('.naam')[0].textContent )) {

                    verwijderRij(rij);

                }

            }
        }

    });

    var navFuncties = {
        option: {
            naam: zoekOpnaam,
            email: zoekOpemail,
            opgroep: zoekOpGroep
        },
        button: {
            nieuweRij: nieuweRij,
            alles: toonAlles,
            selecteerEmail: maakLijstGetoondeMail,
        }
    };

    var selectiesNav = document.getElementById('selecties');

    selectiesNav.addEventListener('click', function(e){

        //toon opties.
        if (e.target.classList.contains('toon-optie')) {
            toonKolommen();
            return;
        }

        var typeKnop = e.target.nodeName.toLowerCase();

        if (typeKnop === 'select') {
            return;
        }

        var functienaam, arg;

        if (typeKnop === 'option') {
            functienaam = e.target.parentNode.name;
            arg = e.target.value;
        } else {
            functienaam = e.target.name;
        }

        if (functienaam === 'afgerond') {
            arg = true;
        } else if (functienaam === 'onafgerond') {
            arg = false;
        }

        if (!functienaam) {
            console.error('functienaam onbekend');
            return;
        }

        //console.log(typeKnop + " " + functienaam);


        var f = navFuncties[typeKnop][functienaam];

        if (typeof f === "function") {
        	f(arg);
        }



    });

    ///////////////////NUTS///////////////////////


    function vindTr(a){

        if (!a) {
            return;
        }
        if (a.nodeName === 'TR') {
            return a;
        }
        while (a.nodeName !== 'TR' && a.nodeName !== 'HTML'){
            a = a.parentNode;
        }
        return a;

    }

    function dce(t){
        return document.createElement(t);
    }

    function naarSlug(a){
        a = a.toLowerCase().replace(/[^\w\s]/gi, '').replace(/\s/gi, '-');
        return a;
    }


    ////////////////////////RIJBEWERKING/////////////////////////

    function pasRijAan(rij){

        var alleTds = rij.getElementsByTagName('td');
        var input, td, tekst, naam;

        for (var i = 2; i < alleTds.length; i++) { //sla aanpassen td over

            td = alleTds[i];
            tekst = td.textContent.trim();
            naam = td.getAttribute('title');

            td.textContent = '';

            if (tekst.length < 30) {
                input = dce('input');
                input.setAttribute('value', tekst);
            } else {
                input = dce('textarea');
                //input.setAttribute('value', tekst);
                input.textContent = tekst;
            }
            input.setAttribute('title', naam);
            input.classList.add('aanpas-input');
            td.appendChild(input);

        }

    }

    function slaRijOp(rij){

        var alleTds = rij.getElementsByTagName('td');
        var input, td, naam, span;

        for (var i = 2; i < alleTds.length; i++) {

            td = alleTds[i];
            input = td.querySelectorAll('input, textarea')[0];

            //is dit wel een opsla actie?
/*			if (!input) {
				pasRijAan(rij);
				return;
			}*/

            naam = input.getAttribute('title');
            tekst = (naam !== 'email' ? input.value : input.value.toLowerCase());
            tekst = (naam === 'vernieuwd' || naam === 'verstuurd' ? tekst.replace(/-/g, ' ') : tekst);

            if (naam === 'naam') {
            	if (tekst === '') {
            		tekst = document.querySelectorAll('.wordt-aangepast')[0].parentNode.querySelectorAll('.email input')[0].value.split('@')[0];
            	}
            }


            td.setAttribute('title', naam);
            td.setAttribute('class', naarSlug(naam));
            td.removeChild(input);
            span = dce('span');
            span.textContent = tekst;
            td.appendChild(span);

            if (naam === 'naam') {
                 rij.id = naarSlug(tekst);
            }

            if (naam === 'groep') {
                rij.className = tekst;
            }

        }

        if (!alleTds[0].getElementsByClassName('icono-tag').length) {
            var iEl;
            iEl = dce('i');
            iEl.className = "icono-tag";
            alleTds[0].appendChild(iEl);
            iEl = dce('i');
            iEl.className = "icono-trash";
            alleTds[1].appendChild(iEl);
        }



    }

    function verwijderRij(rij){

        rij.parentNode.removeChild(rij);

    }

    function nieuweRij(){

        var eersteRij = document.querySelectorAll('#print-hier tr')[0];
        var refId = eersteRij.id;

        var kopie = eersteRij.cloneNode(true);
        kopie.setAttribute('id', '');
        var kopieTds = kopie.getElementsByTagName('td');

        for (var i = 2; i < kopieTds.length; i++) {

            var dezeSpan = kopieTds[i].getElementsByTagName('span')[0];
            if (dezeSpan) {
                dezeSpan.textContent = '';
            }

        }


        if (!kopieTds[0].getElementsByClassName('icono-tag').length) {
            var iEl;
            iEl = dce('i');
            iEl.className = "icono-tag";
            kopieTds[0].appendChild(iEl);
            iEl = dce('i');
            iEl.className = "icono-trash";
            kopieTds[1].appendChild(iEl);
        }

        eersteRij.parentNode.insertBefore(kopie, eersteRij);

        setTimeout(function(){
        	//pasRijAan(kopie);
            document.querySelectorAll('#print-hier tr')[0].getElementsByClassName('aanpassen')[0].click();
        }, 500);

    }


    ///////////////////////ZOEKFUNCTIES////////////////////////////


    function creeerZoekSelects(){

        var naamSel = document.getElementById('naamSel');
        var emailSel = document.getElementById('email');

        var contactNamen = [];

        var alleTrs = document.querySelectorAll('#print-hier tr');

        //leeg alle selects;
        naamSel.innerHTML = '';
        emailSel.innerHTML = '';

        //standaard opt als eerste
        var opt = dce('option');
        opt.textContent = 'naam';
        naamSel.appendChild(opt);

        opt = dce('option');
        opt.textContent = 'Email';
        emailSel.appendChild(opt);



        var selsData = {
            naamSel: {
                select: naamSel,
                namen: [],
                opts: {},
                sort: true
            },
            emailSel: {
                select: emailSel,
                namen: [],
                opts: {},
                sort: true
            },
        };


        for (var i = 0; i < alleTrs.length; i++) {

            var dezeTr = alleTrs[i];

            //naam sel
            opt = dce('option');
            opt.value = dezeTr.id;
            opt.textContent = dezeTr.getElementsByClassName('naam')[0].textContent.trim();
            if (opt.textContent) {
                selsData.naamSel.namen.push(dezeTr.id);
                selsData.naamSel.opts[dezeTr.id] = opt;
            }

/*            //datum sel
            opt = dce('option');
            var dezeDatum = dezeTr.getElementsByClassName('vernieuwd')[0].textContent.trim().substring(0,7);
            opt.value = dezeDatum;

            opt.textContent = dezeDatum.replace(' 01', ' jan').replace(' 02', ' feb').replace(' 03', ' mrt').replace(' 04', ' apr').replace(' 05', ' mei').replace(' 06', ' jun').replace(' 07', ' jul').replace(' 08', ' aug').replace(' 09', ' sep').replace(' 10', ' okt').replace(' 11', ' nov').replace(' 12', ' dec');

            var datOptAr = opt.textContent.split(' ');
            opt.textContent = datOptAr[1] + " " + datOptAr[0];

            if (opt.value) {
                if (selsData.datumSel.namen.indexOf(dezeDatum) === -1) {
                    selsData.datumSel.namen.push(dezeDatum);
                    selsData.datumSel.opts[dezeDatum] = opt;
                }
            }*/

            //email sel
            opt = dce('option');
            var dezeMail = dezeTr.getElementsByClassName('email')[0].textContent.trim();
            opt.value = opt.textContent = dezeMail;
            if (opt.textContent) {
                selsData.emailSel.namen.push(dezeMail);
                selsData.emailSel.opts[dezeMail] = opt;
            }

/*            //bedrijf sel
            opt = dce('option');
            var ditBedrijf = dezeTr.getElementsByClassName('bedrijf')[0].textContent.trim();
            opt.value = opt.textContent = ditBedrijf;
            if (opt.textContent) {
                if (selsData.bedrijfSel.namen.indexOf(ditBedrijf) === -1) {
                    selsData.bedrijfSel.namen.push(ditBedrijf);
                    selsData.bedrijfSel.opts[ditBedrijf] = opt;
                }

            }

            //contact met sel
            opt = dce('option');
            var dezePers = dezeTr.getElementsByClassName('contact-met')[0].textContent.toLowerCase().trim();
            if (contactNamen.indexOf(dezePers) === -1) {
                contactNamen.push(dezePers);
                opt.value = opt.textContent = dezePers;
                if (opt.textContent) {
                    selsData.contactMetSel.namen.push(dezePers);
                    selsData.contactMetSel.opts[dezePers] = opt;
                }
            }*/

        } //eind for

        var n;

        for (var selElnaam in selsData) {

            if (selsData[selElnaam].sort) {
                selsData[selElnaam].namen.sort();
            } else {
                selsData[selElnaam].namen.sort().reverse();
            }


            for (i = 0; i < selsData[selElnaam].namen.length; i++) {
                n = selsData[selElnaam].namen[i];
                selsData[selElnaam].select.appendChild(selsData[selElnaam].opts[n]);
            }

        }

    }

    function toonOfNiet(rij, vgl){
        if (vgl) {
            rij.style.display = "table-row";
        } else {
            rij.style.display = "none";
        }
    }

    function zoekOpnaam(arg){

    	console.log(arg);

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var dezenaam, dezeTr;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            dezenaam = naarSlug(dezeTr.getElementsByClassName('naam')[0].textContent);

            toonOfNiet(dezeTr, arg === dezenaam);

        }
    }

    function zoekOpemail(arg){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var dezeMail, dezeTr;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            dezeMail = dezeTr.getElementsByClassName('email')[0].textContent.trim();

            toonOfNiet(dezeTr, arg === dezeMail);

        }

    }

    function zoekOpGroep(arg){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var dezeGroep, dezeTr;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            dezeGroep = dezeTr.getElementsByClassName('groep')[0].textContent.trim().toLowerCase();

            toonOfNiet(dezeTr, arg === dezeGroep);

        }

    }

    function zoekOpContactMet(arg){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var dezeContactPers, dezeTr, vgl;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            dezeContactPers = dezeTr.getElementsByClassName('contact-met')[0].textContent.toLowerCase().trim();

            if (arg !== 'NIEMAND') {
                vgl = arg === dezeContactPers;
            } else {
                vgl = (arg === dezeContactPers || '' === dezeContactPers);
            }

            toonOfNiet(dezeTr, vgl);

        }

    }

    function zoekOpBedrijf(arg){

        var arg = arg.toLowerCase().trim();

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var ditBedrijf, dezeTr, vgl;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            ditBedrijf = dezeTr.getElementsByClassName('bedrijf')[0].textContent.toLowerCase().trim();

            vgl = (arg === ditBedrijf);

            toonOfNiet(dezeTr, vgl);

        }

    }

    function zoekOpDatum (arg){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var dezeContactPers, dezeTr, vgl, dezeDatum,
        vglDat = Number(arg.replace(' ', ''));

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            dezeDatum = Number(dezeTr.getElementsByClassName('vernieuwd')[0].textContent.trim().substring(0,7).replace(' ',''));
            if (!dezeDatum) {
                dezeDatum = Number(dezeTr.getElementsByClassName('vernieuwd')[0].textContent.trim().substring(0,7).replace(' ',''));
            }
            vgl = vglDat <= dezeDatum;

            toonOfNiet(dezeTr, vgl);

        }

    }

    function toonAfgerond(bool){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var vgl;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];
            var isAfgerond = dezeTr.classList.contains('afgerond');

            toonOfNiet(dezeTr, (isAfgerond === bool));

        }

    }

    function toonActie(){

        var alleTrs = document.querySelectorAll('#print-hier tr');
        var vgl;

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];

            toonOfNiet(dezeTr, (dezeTr.getElementsByClassName('groep')[0].textContent.toLowerCase().indexOf('actie') !== -1));

        }

    }

    function toonAlles(){
        var alleTrs = document.querySelectorAll('#print-hier tr');

        for (var i = 0; i < alleTrs.length; i++) {

            dezeTr = alleTrs[i];

            toonOfNiet(dezeTr, true);

        }
    }

    function toonKolommen (){
        var inputs = document.getElementsByClassName('toon-optie');

        var toon = [];
        var verberg = [];

        for (var i = 0; i < inputs.length; i++) {
            if (inputs[i].checked) {
                toon.push(inputs[i].name);
            } else {
                verberg.push(inputs[i].name);
            }
        }

        var vis = [toon, verberg], dezeTds;

        for (i = 0; i < vis.length; i++) {

            var toonAr = vis[i];

            for (var j = 0; j < toonAr.length; j++) {

                dezeTds = document.querySelectorAll('td.'+toonAr[j]+', th.'+toonAr[j]);

                for (var k = 0; k < dezeTds.length; k++) {

                    dezeTds[k].style.display = (i === 0 ? "table-cell" : "none");

                }


            }


        }

    }

    function maakLijstGetoondeMail(){
    	var alleTr = document.getElementsByTagName('tr');
    	var emails = '';
    	var t;
    	var e;
    	for (var i = 0; i < alleTr.length; i++) {
    		t = alleTr[i];
    		if (t.offsetParent && t.hasAttribute('class')) {
    			e = t.getElementsByClassName('email')[0].textContent.trim();
    			if (e) {
    				emails = emails + ", "+e;
    			}
    		}
    	}
    	emails = emails.substring(2);
    	alert(emails);

    }

    //////////////////////INIT///////////////////////////////

    creeerZoekSelects();
    toonKolommen();

    //verwijder links.
    var head = document.getElementsByTagName('head')[0];
    var verwijder = document.getElementsByTagName('link')[0];
    if (verwijder) {
	    head.removeChild(verwijder);
    }




</script>




